-- Drop and create users table
DROP TABLE IF EXISTS users;
CREATE TABLE users (
    id_user INT(11) PRIMARY KEY AUTO_INCREMENT NOT NULL,
    login VARCHAR(40) NOT NULL,
    name VARCHAR(50),
    email VARCHAR(50),
    password VARCHAR(505),
    create_user DATETIME DEFAULT CURRENT_TIMESTAMP,
