);

-- Drop and create measurements table
DROP TABLE IF EXISTS measurements;
CREATE TABLE measurements (
    id_measurement INT(11) PRIMARY KEY AUTO_INCREMENT NOT NULL,
    measurement VARCHAR(25) NOT NULL,
    id_measurement_type INT(11),
    CONSTRAINT fk_measurement_type_ingredient FOREIGN KEY (id_measurement_type) REFERENCES measurements_type(id_measurement_type) ON DELETE SET NULL
